import React, { useState } from 'react'
import { Link } from 'react-router-dom'; //link to our /chat path

import './Join.css'; //apply all the styles to this component

function Join() {
    const [name, setName] = useState('');
    //we have a name state. We have a function to set this name state.
    //And we are passing it a empty string as a initial value for our name state.
    const [group, setGroup] = useState('');

    return (
        <div className = "joinOuterContainer">
            <div className = "joinInnerContainer">
                <h1 className = "heading">Join</h1>
                <div><input placeholder = "Name" className = "joinInput" type = "text" onChange = { (event) => setName(event.target.value) } /></div>
                <div><input placeholder = "Group" className = "joinInput" type = "text" onChange = { (event) => setGroup(event.target.value) } /></div>

                <Link onClick = { (event) => !name || !group ? event.preventDefault() : null } to = { `/chat?name = ${ name }&group = ${ group }` }> { /* pass data as an url */ }
                    { /* pass parameters in the url using question mark */ }
                    { /* prevent button clicks of this link */ }
                    <button className = "button mt-20" type = "submit">Log In</button>
                </Link>
            </div>
        </div>
    )
}

export default Join
