import React from 'react'
import { useState, useEffect } from 'react'; //for lifecycle methods inside of hooks
import queryString from 'query-string'; //help us getting data from the url
import io from 'socket.io-client';
//use effect is a hook that let us use lifecycle methods in fuctional components
let socket;

function Chat({ location }) {
    //setting up the state
    const [name, setName] = useState('');
    const [group, setGroup] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]); //this is going to be an array
    const ENDPOINT = 'localhost:5000';

    useEffect(() => {
        const { name, group } = queryString.parse(location.search);

        socket = io(ENDPOINT);

        setName(name);
        setGroup(group);

        socket.emit('join', { name, group }, () => {

        });

        return () => {
            socket.emit('disconnect');
            socket.off();
        }
    }, [ENDPOINT, location.search]);  

    useEffect(() => {
        //Keep track of all messages with state.
        socket.on('message', (message) => {
            setMessages([...messages, message]);
            //this is setting every new message to our messages array.
        }, [messages])//only going to run when messages are changed
    })
    //function for sending messages and add our jsx
    //this event handler is simply going to be function
    const sendMessage = (event) => {
        event.preventDefault(); //As full browser refreshes are not good
        //prevent the default behaviour of a keypress or a button
        if(message) socket.emit('sendMessage', message, () => sendMessage(''));
        //when we send a message our input field clears.

    }

    console.log(message, messages);

    return (
        <div className = "outerContainer">
            <div className = "container">
                {/* <input value = { message }
                 onChange = { (event) => setMessage(event.target.value) }
                 onKeyPress = { (event) => event.key ==='Enter' ? sendMessage(event) :null } /> */}

                <InfoBar />


            </div>
        </div>
    )
}

export default Chat
